import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

import Display from './display/Display';
import Keypad from './keypad/Keypad';
import Operators from './operators/Operators';

class App extends Component {
  state = {
    currentCalString: '',
    result: null,
    currentCal: []
  };

  handleOperators = (op) => {
    let sign = ''; 
    switch (op) {
      case 'divide':
        sign = '\u00F7';
        break;
      case 'multiply':
        sign = '\u00D7';
        break;
      case 'minus':
        sign = '\u2212';
        break;
      case 'plus':
        sign = '\u002B';
        break;
      default:
        break;
    }

    this.setState((prevState) => {
      return {
        currentCalString: prevState.currentCalString + ` ${sign} `
      }
    })
  }

  handleKeypad = (key) => {
    if (typeof key !== 'string') {
      this.setState((prevState) => {
        return {
          currentCalString: prevState.currentCalString + key,
          // currentMath : prevState.currentMath.concat()
        }
      });
    }
  }

  render() {
    return (
      <div className="wrapper">
        <div className="container">
          <div className="row">
            <div className="col-sm-6 offset-sm-4">
              <div className="row calculator">
                <Display operations={this.state.currentCalString} result={this.state.result} />
                <Keypad keys={this.handleKeypad} />
                <div className="col-sm4">
                  <Operators operator={this.handleOperators} />
                </div>
              </div>
            </div>
          </div>
        </div> 
      </div>
    )
  }
}

export default App;
