import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

import Display from './display/Display';
import Keypad from './keypad/Keypad';
import Operators from './operators/Operators';

class App extends Component {
  state = {
    currentCalString: '',
    result: null,
    currentCal: []
  };

  handleCalculation = () => {
    let cal = [...this.state.currentCal];
    let total = 0, sign;
    console.log(cal);
    cal.map((ca, index) => {
      console.log('total map', total)
      if (index === 0 && Number(ca)) {
        total = ca;
      }
      if (isNaN(ca)) {
        if (ca === '−') {
          sign = 'minus';
        } else if (ca === '+') {
          sign = 'plus';
        }
      }
      if (Number(ca) && sign !== undefined) {
        console.log('sign2', sign)
        console.log('total', total)
        if (sign === 'minus') {
          total = total - ca;
        } else if (sign === 'plus') {
          total = total + ca;
        }
      }
      return '';
    });
    console.log(total)
  }

  handleOperators = (op) => {
    let sign; 
    switch (op) {
      case 'divide':
        sign = '\u00F7';
        break;
      case 'multiply':
        sign = '\u00D7';
        break;
      case 'minus':
        sign = '\u2212';
        break;
      case 'plus':
        sign = '\u002B';
        break;
      default:
        break;
    }
    this.setState((prevState) => {
      return {
        currentCalString: prevState.currentCalString + ` ${sign} `,
        currentCal: prevState.currentCal.concat(sign)
      }
    })
  }

  handleKeypad = (key) => {
    if (typeof key !== 'string') {
      const last = this.state.currentCal[this.state.currentCal.length - 1];
      console.log(last)
      this.setState((prevState) => {
        return {
          currentCalString: prevState.currentCalString + key,
          currentCal: prevState.currentCal.concat(key)
        }
      });

      // let cal = this.state.currentCalString.split(' ');
      // console.log(cal, this.state.currentCal)
    }
  }

  render() {
    return (
      <div className="wrapper">
        <div className="container">
          <div className="row">
            <div className="col-sm-6 offset-sm-4">
              <div className="row calculator">
                <Display operations={this.state.currentCalString} result={this.state.result} />
                <Keypad keys={this.handleKeypad} />
                <div className="col-sm4">
                  <Operators operator={this.handleOperators} calculate={this.handleCalculation} />
                </div>
              </div>
            </div>
          </div>
        </div> 
      </div>
    )
  }
}

export default App;
