import React from 'react';
import moment from 'moment';
import './Date.scss';

// const Date = () => {
//   return (
//     <div className="today">
//       <h3>12:29 <span>PM</span></h3>
//       <h1>Monday, 18 August 2018</h1>
//     </div>
//   )
// }

class Date extends React.Component {
  constructor() {
    super();
    this.state = {
      currentTime: moment()
    };
  };


  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      5000
    );
    // window.setInterval(() => this.setState({ currentTime: moment() }), 5000)
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      currentTime: moment()
    });
  }

  render() {
    const { currentTime } = this.state;
    return (
      <div className="today">
        <h3>{ currentTime } <span>PM</span></h3>
        <h1>Monday, 18 August 2018</h1>
      </div>
    );
  }
}

export default Date;
