import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

import Background from './background/Background';
import Status from './status/Status';
import Date from './date/Date';
import Forecasts from './forecasts/Forecasts';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      weatherData: {},
    };

    this.getWeatherInfo();
  }

  // componentDidMount() {
  //   this.getWeatherInfo();
  // }

  async getWeatherInfo() {
    const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?id=2147714&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0`).then(res => res.json());
    // const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&long=${long}&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0`).then(res => res.json());

    console.log(res)

    const weatherInfo = {
      temp: res.main.temp,
      desc: res.weather[0].main,
      icon: `wi-owm-${res.weather[0].icon}`
    };

    this.setState({
      weatherData: weatherInfo
    })
  }

  render() {
    return (
      <React.Fragment>
        <Background />
        <div className="container">
          <div className="row">
            <div className="col-8">
              <Date />
            </div>
            <div className="col-4">
              <Status weatherData={this.state.weatherData} status="Clear Sky" degree="16" location="Lagos Island, Nigeria" />
            </div>
          </div>
        </div>
        <div className="forecastsWrapper">
          <div className="col-12">
            <Forecasts />
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
