import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

import Background from './background/Background';
import Status from './status/Status';
import Date from './date/Date';
import Forecasts from './forecasts/Forecasts';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      weatherData: {},
    };

    this.getWeatherInfo();
    console.log(this.getLocation())
  }

  // componentDidMount() {
  //   this.getWeatherInfo();
  // }

   getLocation() {
    const geolocation = navigator.geolocation;

    // const location = new Promise((resolve, reject) => {
    //     if (!geolocation) {
    //         reject(new Error('Not Supported'));
    //     }

    //   geolocation.getCurrentPosition((position) => {
    //       resolve(position);
    //   }, () => {
    //       reject (new Error('Permission denied'));
    //   });
    // });

    const where =  geolocation.getCurrentPosition(postion => {
      console.log('loca', postion);
      return postion;
    });

  return {
    payload: where
  }
};

  async getWeatherInfo() {
    const res = await fetch(`https://api.openweathermap.org/data/2.5/weather?id=2147714&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0`).then(res => res.json());

    // https://api.openweathermap.org/data/2.5/weather?lat=${lat}&long=${long}&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0&cnt=7`

    // console.log(res)

    const weatherInfo = {
      temp: res.main.temp,
      desc: res.weather[0].main,
      icon: `wi-owm-${res.weather[0].id}`
    };

    this.setState({
      weatherData: weatherInfo
    })
  }

  render() {
    return (
      <React.Fragment>
        <Background />
        <div className="container">
          <div className="row">
            <div className="col-8">
              <Date />
            </div>
            <div className="col-4">
              <Status weatherData={this.state.weatherData} status="Clear Sky" degree="16" location="Lagos Island, Nigeria" />
            </div>
          </div>
        </div>
        <div className="forecastsWrapper">
          <div className="col-12">
            <Forecasts />
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
