class Date extends React.Component {
  state = {
    currentTime: moment()
  };

  componentDidMount() {
    // this.timerID = setInterval(
    //   () => this.tick(),
    //   5000
    // );
    window.setInterval(() => this.setState({ currentTime: moment() }), 5000)
  }

  componentWillUnmount() {
    // clearInterval(this.timerID);
  }

  // tick() {
  //   this.setState({
  //     date: new Date()
  //   });
  // }

  render() {
    return (
      <div className="today">
        <h3>{ this.state.currentTime } <span>PM</span></h3>
        <h1>Monday, 18 August 2018</h1>
      </div>
    );
  }
}

export default Date;
