import React from 'react';
import moment from 'moment';
import './Background.scss';
import city01 from '../../assets/images/city01.png';

const timeOfDay = () => {
  let time = moment().format('A').toLocaleLowerCase();
  console.log(time)
}

const Background = () => {
  timeOfDay()
  const styles = {
    backgroundImage: "url(" + city01 + ")",
    backgroundSize: 'cover',
    width: '100%',
    height: '100vh',
  }

  return (
    <React.Fragment>
      <div className="wrapper" style={styles}>
        <div className="overlay blue"></div>
      </div>
    </React.Fragment>
  )
}

export default Background;
