import React from 'react';
import './Forecasts.scss';
import Forecast from '../forecast/Forecast';

const getWeekDays = (index) => {
    var baseDate = new Date(Date.now()); // just a Monday
    var weekDays = [];
    for(let i = 0; i < 7; i++) {       
      weekDays.push(baseDate.toLocaleDateString('en-us', { weekday: 'long' }));
      baseDate.setDate(baseDate.getDate() + 1);       
    }
    return weekDays[index];
}

const Forecasts = (props) => {
  var days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  const forecastData = props.forecasts;
  console.log(forecastData)

  return (
    <div className="forecastWrapper">
      { forecastData ?
          forecastData.map((forecast, index) => (
            <Forecast key={forecast.dt} day={getWeekDays(index)} />
          ))
        : null
      }
    </div>
  )
}

export default Forecasts;
