import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

import Background from './background/Background';
import Status from './status/Status';
import Date from './date/Date';
import Forecasts from './forecasts/Forecasts';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      weatherData: {},
      forecastdata: {}
    };

    // this.getLocation();
  }

  componentDidMount() {
    this.getLocation();
  }

   getLocation() {
    const geolocation = navigator.geolocation;
    geolocation.getCurrentPosition(position => {
      this.getWeatherInfo(position.coords.latitude, position.coords.longitude)
    });
  };

  async getWeatherInfo(lat, long) {
    const oneData = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0&cnt=7`).then(res => res.json());

    const res = await fetch(`https://api.openweathermap.org/data/2.5/forecast/daily?lat=${lat}&lon=${long}&units=metric&appid=c5baa00af2bfbc51b5a8bff68a069bb0&cnt=7`).then(res => res.json());

    console.log(res)

    const weatherInfo = {
      temp: oneData.main.temp,
      desc: oneData.weather[0].description,
      icon: `wi-owm-${oneData.weather[0].id}`
    };

    // const forecastData = {
    //   temp: res.list[0].main.temp,
    //   desc: res.weather[0].description,
    //   icon: `wi-owm-${res.weather[0].id}`
    // };

    this.setState({
      weatherData: weatherInfo
    })
  }

  render() {
    return (
      <React.Fragment>
        <Background />
        <div className="container">
          <div className="row">
            <div className="col-8">
              <Date />
            </div>
            <div className="col-4">
              <Status weatherData={this.state.weatherData} status="Clear Sky" degree="16" location="Lagos Island, Nigeria" />
            </div>
          </div>
        </div>
        <div className="forecastsWrapper">
          <div className="col-12">
            <Forecasts forecastData />
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App;
