import React from 'react';
import './Date.scss';

// const Date = () => {
//   return (
//     <div className="today">
//       <h3>12:29 <span>PM</span></h3>
//       <h1>Monday, 18 August 2018</h1>
//     </div>
//   )
// }

class Date extends React.Component {
  state = {
    date: new Date()
  };

  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  render() {
    return (
      <div className="today">
        <h3>{ this.state.date.toLocaleTimeString() } <span>PM</span></h3>
        <h1>Monday, 18 August 2018</h1>
      </div>
    );
  }
}

export default Date;
